<template>  <view class="area-session">    <text class="title">区域会场</text>    <text class="tips">点击专场进入相应分会场</text>    <view class="list"><image v-for="(item, index) in images" :key="index" :src="item" mode="aspectFill" @click="handleItemClick(index)"></image></view>  </view></template><script>export default {  data() {    return {      images: [        '/static/index/banner-index-zhusanjiao-session.png',        '/static/index/banner-index-zhongbu-session.png',        '/static/index/banner-index-sanqusanzhou-session.png',        '/static/index/banner-index-yuegangao-session.png',        '/static/index/banner-index-hubei-session.png',        '/static/index/banner-index-jinjingji-session.png',        '/static/index/banner-index-pinkunxian-session.png',        '/static/index/banner-index-changsanjiao-session.png',        '/static/index/banner-index-dongbei-session.png'      ]    };  },  methods: {    handleItemClick(index) {      const sessions = ['zhusanjiao', 'zhongbu', 'sanqusanzhou', 'yuegangao', 'hubei', 'jinjingji', 'pinkunxian', 'changsanjiao', 'dongbei'];      uni.navigateTo({        url: `session?session=${sessions[index]}`      });    }  }};</script><style lang="scss" scoped>.area-session {  padding: 0 30upx;  display: flex;  flex-direction: column;  .title {    margin: 16upx 0;    font-size: 36upx;    font-weight: bold;    color: #292626;  }  .tips {    font-size: 24upx;    color: #4077f4;  }  .list {    padding-bottom: 64upx;    display: flex;    flex-wrap: wrap;    image {      margin-top: 30upx;      width: 690upx;      height: 128upx;      border-radius: 8upx;    }  }}</style>
